import React from 'react';
import { Row, Col } from 'reactstrap';
import {
    Link
} from 'react-router-dom'
import styled from 'styled-components';


import Image from '../public/img.jpg'

const FormLogin = styled.div`
    width : 900px;
    height : 500px;
    background-color : #f8f8f8;
    border-radius : 20px;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    box-shadow: 0px 0px 50px rgba(0, 0, 0, 0.3);
`;

const InputItems = styled.div`
    margin-top : 20px;
`;

const ContainerMiddle = styled.div`
    padding : 50px;
`;

const CustomInput = styled.input`
    name : ${props => props.name || ""};
    width: ${props => props.width || "100%" };
    height:  ${props => props.height || "50%" };
    background: #F4F4F4;
    border-width:0px;
    border-radius: 10px;
    padding : 10px;
    cursor: pointer;
    &:hover{
        box-shadow: inset 0px 0px 10px rgba(0, 0, 0, 0.25);
        transition-duration : 0.2s;
    }
`;

const NormalTextBlack = styled.span`
    color : #242424;
    font-size : ${props => props.fontSize || "16px"};
    font-weight : ${props => props.fontWeight || "300"};
    font-style : normal;
    transition-duration : 0.3s;

    &:hover{
        color : #9a9a9a;
    }
`;

const NormalTextLight = styled(NormalTextBlack)`
    color : #F8F8F8;
`

const TitleH1 = styled.h1`
    font-weight : 500;
    font-style : normal;
    line-height : 58.59px;
`;

const PrimaryButton = styled.div`
    display: block;
    width: 100%;
    border: none;
    background-color: #242424;
    color: white;
    padding: 10px 20px ;
    font-size: 16px;
    cursor: pointer;
    text-align: center;
    border-radius : 20px;

    &:hover{
        box-shadow: 0px 10px 30px rgba(0, 0, 0, 0.3);
    }
`
const SecondaryButton = styled(PrimaryButton)`
    background-color: #f8f8f8;
    border: 1px solid #242424;

`

const BigImage = styled.div`
    width : 100%;
    height : 500px;
    border-radius : 0px 20px 20px 0px;
    background-size : cover;
    background-image : url(${props => props.url || ""} );
`

export default function Login() {
    return (    
        <div>
            <FormLogin >
            <Row lg="2">
                <Col>
                <ContainerMiddle>
                    <TitleH1 >Login</TitleH1>
                    <NormalTextBlack fontWeight ="300" fontSize = "14px">Starting manage your team</NormalTextBlack>
                    <InputItems>
                        <NormalTextBlack fontWeight ="500" fontSize = "16px">Username</NormalTextBlack>
                        <CustomInput name="username" height = "40px" />
                    </InputItems>
                    <InputItems>
                        <NormalTextBlack fontWeight ="500" fontSize = "16px">Password</NormalTextBlack>
                        <CustomInput name="password" type="password"  height = "40px" />
                    </InputItems>
                    <InputItems>
                        <NormalTextBlack fontWeight ="500" fontSize = "14px">Fogot Password ?</NormalTextBlack>
                    </InputItems>
                    <InputItems>
                    <Row>
                        <Col lg="8">
                            <PrimaryButton>
                                <NormalTextLight fontWeight = "500" fontSize="14px">Sign In</NormalTextLight>
                            </PrimaryButton>
                        </Col>
                        
                        <Col lg="4">
                            <SecondaryButton>
                                    <Link to='/register'>
                                    <NormalTextBlack fontWeight = "300" fontSize="14px">
                                    Sign Up
                                    </NormalTextBlack>
                                    </Link>
                            </SecondaryButton>
                        </Col>
                    </Row>
                    </InputItems>
                </ContainerMiddle>
                </Col>
                <Col>
                    <BigImage url={Image} ></BigImage>
                </Col>
            </Row>
            </FormLogin>
        </div>
    )
}
