import React from 'react';
import { Row, Col } from 'reactstrap';
import styled from 'styled-components';

const FormLogin = styled.div`
    width : 900px;
    height : 500px;
    background-color : #f8f8f8;
    border-radius : 20px;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
`;

const InputItems = styled.div`
    margin-top : 10px;
`;

const ContainerMiddle = styled.div`
    padding : 50px;
`;

const CustomInput = styled.input`
    width: ${props => props.width || "100%" };
    height:  ${props => props.height || "50%" };
    background: #F4F4F4;
    border-width:0px;
    border-radius: 10px;
    padding : 10px;
    &:hover{
        box-shadow: inset 0px 0px 10px rgba(0, 0, 0, 0.25);
        transition-duration : 0.2s;
    }
`;

const FormAlign = styled.div`
    display: flex;
    flex-direction : column;
    align-items: flex-start; /* vertical */
    justify-content: center; /* horizontal */
`;

const NormalTextBlack = styled.p`
    color : #242424;
    font-size : ${props => props.fontSize || "16px"};
    font-weight : ${props => props.fontWeight || "300"};
    font-style : normal;
`;

const NormalTextLight = styled(NormalTextBlack)`
    color : #F8F8F8;
`

const TitleH1 = styled.h1`
    font-weight : 500;
    font-style : normal;
    line-height : 58.59px;
`;

const PrimaryButton = styled.div`
    display: block;
    width: 100%;
    border: none;
    background-color: #242424;
    color: white;
    padding: 14px 28px;
    font-size: 16px;
    cursor: pointer;
    text-align: center;

    &:hover{
        box-shadow: 0px 10px 30px rgba(0, 0, 0, 0.3);
    }
`

export default function Login() {
    return (    
            <FormLogin >
            <Row lg="2">
                <Col>
                <ContainerMiddle>
                    <TitleH1 >Login</TitleH1>
                    <NormalTextBlack fontWeight ="300" fontSize = "14px">Starting manage your team</NormalTextBlack>
                    <FormAlign>
                        <InputItems>
                            <NormalTextBlack fontWeight ="500" fontSize = "16px">Username</NormalTextBlack>
                            <CustomInput width = "300px" height = "40px" />
                        </InputItems>
                        <InputItems>
                            <NormalTextBlack fontWeight ="500" fontSize = "16px">Password</NormalTextBlack>
                            <CustomInput type="password" width = "300px" height = "40px" />
                        </InputItems>
                        <InputItems>
                            <PrimaryButton>
                                <NormalTextLight fontWeight = "500" fontSize="16px">Sign In</NormalTextLight>
                            </PrimaryButton>                        
                        </InputItems>
                    </FormAlign>
                </ContainerMiddle>
                </Col>
                <Col>Image</Col>
            </Row>
            </FormLogin>
    )
}
