export const activeItem = atom({
    key: 'activeItem', // unique ID (with respect to other atoms/selectors)
    default: {
        employee : [
            {   
                id : "1",
                href : "dashboard/employee/list",
                name : "List",
                active : false
            },
            {
                id : "1",
                href : "dashboard/employee/edit",
                name : "Edit",
                active : false
            }
        ],
        salary : [
            {   
                id : "1",
                href : "dashboard/salary/list",
                name : "List",
                active : false
