import React, {useEffect} from 'react'
import {BsPlus} from 'react-icons/bs'
import {CommonCard} from '../../components/cards/CommonCard'
import {CommonButtonBlack} from '../../components/buttons/index'
import { Table } from 'reactstrap';
import ReactLoading from 'react-loading';
import { toast } from 'react-toastify';

import { useQuery  } from '@apollo/client';
//gql
import {GET_ALL_EMPLOYEES} from '../../graphql/query/index'

export default function Employee() {

    const { loading, error, data } = useQuery(GET_ALL_EMPLOYEES);
    
    // const getAllEmployees = {data}
    // console.log(data.getAllEmployees)
    if (error){
        toast(`⛔ ${error.message}`);
    }

    return (
        <div style={{overflowX : "scroll" , padding : "10px"}}>
            
            <CommonCard width={"100%"}>
            {loading ? <ReactLoading type={"bars"} color={"#242424"} height={50} width={50} /> : 
                <>
                <CommonButtonBlack
                    width="100px"
                    height ="40px"
                >
                    <BsPlus size="24" color="#f8f8f8"/>
                    Add
                </CommonButtonBlack>
                <Table hover responsive>
                    <thead>
                        <tr>
                        <th>#</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Position</th>
                        </tr>
                    </thead>
                    {
                        data.getAllEmployees.map((i ,index) => 
                        <tbody>
                            <tr>
                            <th scope="row">{index+1}</th>
                            <td>{i.name}</td>
                            <td>{i.email}</td>
                            <td>{i.position}</td>
                            </tr>
                        </tbody>
                        )
                    }
                </Table>
                </>
            }
            
            </CommonCard>
        </div>
    )
}
