import React from 'react'
import styled from 'styled-components'

import {TextBlack} from '../text/index'

const Card = styled.div`
    width : 100%;
    height : 200px;
    border-radius : 10px;
    background-color : #f8f8f8;
    padding : 20px 30px ;

    &:hover{
        box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.15);   
    }
`

export default function AvatarSquareCard({avatar , name , position, email}) {
    return (
        <div>
            <Card>
                <div className="">This is avatar</div>
                <TextBlack fontSize="14px" fontWeight ="500">Vo Phu Hoang Nhat</TextBlack>
            </Card>
        </div>
    )
}
