import {Row , Col} from 'reactstrap';
import styled from 'styled-components';
import { useRecoilState, useRecoilValue} from 'recoil'
import {FiSearch} from 'react-icons/fi'
import {IoNotificationsOutline} from 'react-icons/io5'
import Avatar from 'react-avatar';

import {AvatarRectangleCard} from '../cards/AvatarCard'
import {TextBlack} from '../text/index'
import {CustomInput} from '../login/index'


const NavBarStyled = styled.nav`
    display : flex;
    align-items : center;
    width : 100%;
    padding : 10px 20px;
    position : fixed ;
    top : 0;
    left : 0;
    background : #ffff;
    z-index : 3;
`
const NavBarMidItems = styled.div`
    display : flex;
    padding : 0px;
    align-items : center;
    justify-content : space-between;
`
const NavBarEndItems = styled(NavBarMidItems)`
    justify-content : space-around;
    padding : 0 10px;
`

export const NavBar= () =>{
    
    return (
    <Row>
    <NavBarStyled>
        <Col lg="2">
            <TextBlack fontSize ="20px" fontWeight ="500">
                Coffee System
            </TextBlack>
        </Col>
        <Col lg="7">
            <NavBarMidItems>
            <TextBlack fontSize="24px" fontWeight="500">Welcome ! user</TextBlack>
            <div className="input-custom">
                <FiSearch size="20" color="#242424"/>
                <CustomInput
                    style = {{marginLeft : "30px"}}
                    name = "Search"
                    width = "300px"
                    height = "35px"
                />
            </div>
            
            </NavBarMidItems>
        </Col>
        <Col lg="3">
            <NavBarEndItems>
            <IoNotificationsOutline style={{margin: "0 10px"}} size="30px"/>
            <AvatarRectangleCard/>
            </NavBarEndItems>
        </Col>
    </NavBarStyled>
    </Row>
    )
}
