import {action} from 'easy-peasy'
export default  {
    info : {
        id : "",
        username : "",
        position : "Admin"
    },
    login : action((state, payload) => {
        if(payload){
            state.info.id = payload.id,
            state.info.username = payload.username,
            state.info.position = payload.position
        }
    })
}
