import React, { useEffect } from "react";
import { ToastContainer} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect 
} from "react-router-dom";
import {PrivateRoute , PrivateRouteLogin} from './components/routes/PrivateRoute'

import Cookies from 'js-cookie'


import { useRecoilState } from 'recoil'

//pages
import Login from './pages/login'
import Register from './pages/register'
import Dashboard from './pages/dashboard'

import {loginState} from './states/adminState'
// import isAuth from './components/functions/isAuth'


export default function App() {
  const [auth, setAuth] = useRecoilState(loginState)
  //read cookies b4 render
  useEffect(() => {
    const isCookie = Cookies.get('accessToken')
    if(isCookie){
      setAuth(true)
    }
  }, [])
  //undefined
  return (
    <Router>
      <div>
        <ToastContainer 
            position = "top-right"
            hideProgressBar = {true}
            closeOnClick = {true}
            pauseOnHover = {true}
            draggable = {true}
            autoClose = {3000}
            />
        <Switch>
          <Route  path="/register" component={Register}/>
          <Route  path="/login" component={Login}/>
          {/* <Route  path="/dashboard" component={Dashboard}/> */}
          <PrivateRoute
            path="/dashboard"
            isAuth = {auth}
            component = {Dashboard}
          />
          <PrivateRoute
            exact
            path="/"
            isAuth={auth}
            component = {Dashboard}
          />
          {/* <Route 
              exact
              path="/"
              render = {()=>{
                return (
                  // check.isAuth ? 
                  Cookies.get('accessToken') ?
                  <Dashboard/> :
                  <Redirect to="/login" /> 
                )
              }
            }
          />
          <Route 
              path="/dashboard"
              render = {()=>{
                return (
                  // check.isAuth ? 
                  Cookies.get('accessToken') ?
                  <Dashboard/> :
                  <Redirect to="/login" /> 
                )
              }
            }
          /> */}
          <Route path="*" >
            <h1>404 - Page not found</h1>
          </Route>
        </Switch>
      </div>
    </Router>
  );
}
