import {atom} from 'recoil'

export const activeItem = atom({
    key: 'activeItem', // unique ID (with respect to other atoms/selectors)
    default: [
            {
                href : "./employee",
                name : "Employee",
                active : false
            },
            {
                href : "./request",
                name : "Request",
                active : false
            },
        ], // default value (aka initial value)
        
    });
