import React, {useState} from 'react'
import styled from 'styled-components'

import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import {SideBarStyled} from '../sidebar'
import { TextBlackUnderline} from '../text/index'
import  AvatarSquareCard from '../cards/AvatarSquareCard'
import { ReactComponent as DevIcon} from '../../public/design-team.svg';

const InfoBarStyled = styled(SideBarStyled)`
    width : 337px;
    overflow : hidden;

    &:hover{
        overflow-y : scroll;
        overflow-x : hidden
    }

`
const ItemInfoBarStyled = styled.div`
    display : flex;
    flex-direction : column;
    align-items : center;
    margin : 10px 0;
`
//height : 595px
//width : 225px 
// margin-left : 1128px2
export  function InfoSideBar() {
    const [value, onChange] = useState(new Date());
    return (
        <div>
            <InfoBarStyled>
                <ItemInfoBarStyled>
                    <TextBlackUnderline fontSize="16px">Profile</TextBlackUnderline>
                    <AvatarSquareCard/>
                </ItemInfoBarStyled>
                <ItemInfoBarStyled>
                <TextBlackUnderline fontSize="16px">Calendar</TextBlackUnderline>
                    <Calendar
                        style={{maxWidth : "80%"}}
                        onChange={onChange}
                        value={value}
                    />
                </ItemInfoBarStyled>
                <ItemInfoBarStyled>
                <TextBlackUnderline fontSize="16px">Developing</TextBlackUnderline>
                    <DevIcon style ={{ width: "230px" , height : "250px"}}/>
                </ItemInfoBarStyled>
            </InfoBarStyled>
        </div>
    )
}
