// import React, {useState} from 'react';
import { Row, Col } from 'reactstrap';
import {
    Link
} from 'react-router-dom'
import { ApolloClient, createHttpLink, InMemoryCache } from '@apollo/client';
import { setContext } from '@apollo/client/link/context';
import { useForm } from "react-hook-form";

//import components
import Image from '../public/img.jpg'
import {
    FormLogin,
    ContainerMiddle,
    TitleH1,
    BigImage,
    InputItems,
    CustomInput
} from '../components/login'
import { 
    PrimaryButton,
    SecondaryButton
 } from "../components/buttons";
import { 
    TextBlack,
    TextLight,
    TextBlackHover,
    TextError
 } from "../components/text";

//  const { loading, error, data, refetch } = useQuery(GET_DOG_PHOTO, {
//     variables: { breed }
//   });


export default function Login() {
    const { register, handleSubmit, watch, errors } = useForm();
    const onSubmit = (data) => {
        console.log(data)
    }

    return (    
        <div>
            <FormLogin onSubmit={handleSubmit(onSubmit)} >
            <Row lg="2">
                <Col>
                <ContainerMiddle>
                    <TitleH1 >Login</TitleH1>
                    <TextBlack fontWeight ="300" fontSize = "14px">Starting manage your team</TextBlack>
                    <InputItems>
                        <TextBlack fontWeight ="500" fontSize = "16px">Username</TextBlack>
                        <CustomInput  name="username" height = "40px" placeholder="Username ..." ref={register} />
                        {errors.username && <TextError>This field is required</TextError>}
                    </InputItems>
                    <InputItems>
                        <TextBlack fontWeight ="500" fontSize = "16px">Password</TextBlack>
                        <CustomInput  name="password" type="password"  height = "40px" placeholder="Password ..." ref={register} />
                        {errors.password && <TextError>This field is required</TextError>}
                    </InputItems>
                    <InputItems>
                        <TextBlackHover fontWeight ="500" fontSize = "14px">Fogot Password ?</TextBlackHover>
                    </InputItems>
                    <InputItems>
                    <Row>
                        <Col lg="8">
                            <Link to='/dashboard'>
                                <PrimaryButton> 
                                    <TextLight fontWeight = "500" fontSize="14px">Sign In</TextLight> 
                                </PrimaryButton>
                            </Link>
                        </Col>
                        
                        <Col lg="4">
                            <Link to='/register'>
                                <SecondaryButton type="submit">
                                    <TextBlack fontWeight = "300" fontSize="14px">
                                        Sign Up
                                    </TextBlack>
                                </SecondaryButton>
                            </Link>
                        </Col>
                    </Row>
                    </InputItems>
                </ContainerMiddle>
                </Col>
                <Col>
                    <BigImage url={Image} ></BigImage>
                </Col>
            </Row>
            </FormLogin>
        </div>
    )
}
