import {atom} from 'recoil'

export const itemSideBar = atom({
    key: 'itemSideBar', // unique ID (with respect to other atoms/selectors)
    default: [
            {   
                id : "0",
                href : "/dashboard",
                name : "Home"
            },
            {   
                id : "1",
                href : "/dashboard/employee",
                name : "Employee"
            },
            {
                id : "2",
                href : "/dashboard/request",
                name : "Request",
            },
        ], // default value (aka initial value)
        
    });

export const itemNavbar = atom({
    key: 'itemNavbar', // unique ID (with respect to other atoms/selectors)
    default: [
            {
                id : "0",
                name : "Home",
                href : "/dashboard"
            },
            {
                id : "1", //employee
                name : "List",
                href :"/employee/list"
            },
            {
                id : "1", //employee
                name : "Shift",
                href :"/employee/shift"
            },
            {
                id : "2", //request
                name : "List",
                href :"/request/list"
            },
        ], // default value (aka initial value)
        
    });

export const clickSideBar = atom({
    key : "clickSideBar",
    default : {
        sideBar : 0,
        navBar : 0
    }
})
