import React from 'react';
import { Container, Row, Col } from 'reactstrap';
import styled from 'styled-components';

const FormLogin = styled.div`
    width : 900px;
    height : 500px;
    background-color : #f8f8f8;
    border-radius : 20px;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
`;

const InputItems = styled.div`
    margin : 40px 0;
`;

const ContainerMiddle = styled.div`
    padding : 50px;
`;

const CustomInput = styled.input`
    width: ${props => props.width || "100%" };
    height:  ${props => props.height || "50%" };
    background: #F4F4F4;
    border-width:0px;
    border-radius: 10px;
    padding : 10px;
    &:hover{
        box-shadow: inset 0px 0px 10px rgba(0, 0, 0, 0.25);
        transition-duration : 0.2s;
    }
`;

const FormAlign = styled.div`
    display: flex;
    flex-direction : column;
    align-items: flex-start; /* vertical */
    justify-content: center; /* horizontal */
`;

const NormalText = styled.p`
    color : #f8f8f8;
    font-size : ${props => props.fontSize || "16px"};
    font-weight : 500;
    font-style : normal;
`;

const LightText = styled(NormalText)`
    color : #a0a0a0;
    font-weight : 300;
`;

const TitleH1 = styled.h1`
    font-weight : 500;
    font-style : normal;
    line-height : 58.59px;
`;

const PrimaryButton = styled.div`
    width : 100px;
    padding : 10px 20px;
    background-color: #242424;

    &:hover{
        box-shadow: 0px 0px 40px rgba(0, 0, 0, 0.61);
    }
`

export default function Login() {
    return (    
            <FormLogin >
            <Row lg="2">
                <Col>
                <ContainerMiddle>
                    <TitleH1 >Login</TitleH1>
                    <LightText fontSize = "12px">Starting manage your team</LightText>
                    <FormAlign>
                        <InputItems>
                            <NormalText fontSize = "16px">Username</NormalText>
                            <CustomInput width = "300px" height = "40px" />
                        </InputItems>
                        <InputItems>
                            <NormalText>Password</NormalText>
                            <CustomInput type="password" width = "300px" height = "40px" />
                        </InputItems>
                        <InputItems>
                            <PrimaryButton>
                                <NormalText fontSize="30px">Sign In</NormalText>
                            </PrimaryButton>                        
                        </InputItems>
                    </FormAlign>
                </ContainerMiddle>
                </Col>
                <Col>Image</Col>
            </Row>
            </FormLogin>
    )
}
