import styled from 'styled-components';
import { AiOutlineHome } from "react-icons/ai";
import { useRecoilState } from 'recoil'

import {
    Link
} from 'react-router-dom'

import {activeItem} from '../../states/navbarState'

const SideBarStyled = styled.div`
    display : flex;
    flex-direction : column;
    align-items : center;
    height : 595px;
    justify-content : space-between;
    background-color : #ffff;
    width  : 190px;
    position : fixed;
    /* height : 100%; */
`
const SideBarItem = styled.div`
    display : flex;    
    border-radius: 15px;
    justify-content: space-around;
    padding : 15px 30px;
    margin-top : 30px;
    align-items : center;
    cursor : pointer;
    
    background-color : ${props => props.active ? "#242424" : "#ffff"};
    transition-duration : 0.2s;
    &>a,svg{
        font-weight : 500;
        color : ${props => props.active ? "#f8f8f8 !important" : "#242424 !important"};
        text-decoration : none;
    }
    &:hover{
        &>a,svg{   
            color : #f8f8f8 !important;
        }
        background-color : #242424;
        text-decoration : none;
    }

`



export default function SideBar({items}) {
    //func
    const [itemState , setItemState] = useRecoilState(activeItem)
    const handleClick = (id) => {
        const newList = itemState.map((item) => {
            const newItem = item ;
            newItem.active === false;
            
            if (newItem.id === id) {
            const updatedItem = {
                ...newItem,
                active: true,
            };
    
            return updatedItem;
            }
    
            return item;
        });
    
        setItemState(newList);
    }
    return (
        <div className="">
            <SideBarStyled>
                <div className="top" style={{width : "90%"}}>
                    {
                        itemState.map((item)  =>
                        <SideBarItem active={item.active} >
                            <AiOutlineHome size="20px" color = "#F8f8f8"/>
                            <Link onClick={()=>handleClick(item.id)} to ={item.href}>{item.name} </Link>
                        </SideBarItem>
                        )
                        
                    }
                    {/* <SideBarItem>
                        <AiOutlineHome size="20px" color = "#F8f8f8"/>
                        <Link to ="./">Home</Link>
                    </SideBarItem>
                    <SideBarItem>
                        <AiOutlineHome size="20px" color = "#F8f8f8"/>
                        <Link to ="./employee">Employee</Link>
                    </SideBarItem>
                    <SideBarItem>
                        <AiOutlineHome size="20px" color = "#F8f8f8"/>
                        <Link to ="./salary">Salary</Link>
                    </SideBarItem> */}
                </div>
                <div className="bot" style={{width : "90%"}}>
                    <SideBarItem>
                        <AiOutlineHome size="20px" color = "#F8f8f8"/>
                        <Link to ="">Log out</Link>
                    </SideBarItem>
                </div>
                
            </SideBarStyled>
        </div>
    )
}
