import styled from 'styled-components';
import { useRecoilState } from 'recoil'

import {activeItem} from '../states/navbarState'

import {TextBlack} from '../text/index'
import {CustomInput} from '../login/index'
// const SideBarStyled = styled`
    
// `

const NavBarStyled = styled.nav`
    display : flex;
    align-items : center;
    width : 100%;
    padding : 20px;
    position : fixed ;
    top : 0;
    left : 0;
    background : #ffff;
`
const NavBarItemsStyled = styled.a`
    display : inline-flex;
    text-decoration : none !important;
    margin : 0 20px;
    padding : 10px 20px;
    color : #242424;
    border-radius : 15px;
    cursor : pointer;
    background-color : ${props => props.active ? "#fcfcfc" : "#fffff"};
    transition-duration : 0.3s;
    &:hover{
        color : #242424;
        background-color : #fcfcfc;
        box-shadow: 0px 14px 28px rgba(0, 0, 0, 0.15);
    };

    &:focus {
      color: #242424;
    }
`

//funcs
const [items , setItems ] = useRecoilState(activeItem)
const onItemClick = (id) => {
    const newList = items.map((item) => {
        item.active = false
        if (item.id === id) {
          const updatedItem = {
            ...item,
            active: !item.active,
          };
   
          return updatedItem;
        }
   
        return item;
      });
   
      setItems(newList);

}

export const NavBar= ({items}) =>{
    return (
        <>
        <NavBarStyled>
            <TextBlack fontSize ="20px" fontWeight ="500">
                Coffee System
            </TextBlack>
            <CustomInput
                style = {{marginLeft : "30px"}}
                name = "Search"
                width = "300px"
                height = "35px"
            />
            {
                items.map(item => 
                <NavBarItemsStyled 
                    onClick = {onItemClick}
                    active={item.active} 
                    href={item.href}
                >
                {item.name}
                </NavBarItemsStyled>)
            }
            
        </NavBarStyled>
        </>
    )
}


export const SideBar= () =>{
    return (
        <>
        <sideBarStyled>
            This is sidebar
        </sideBarStyled>
        </>
    )
}
