    }
    return (
        <div className="">
            <SideBarStyled>
                <div className="top" style={{width : "90%"}}>
                    {
                        items.map((item)  =>
                        <SideBarItem >
                            <Link onClick={handleClick(item.id)} to ={item.path}>
                                <AiOutlineHome active={item.active ? true : false} size="20px" color = "#F8f8f8"/>
                                {item.name}
                            </Link>
                        </SideBarItem>
                        )
                        
                    }
                    {/* <SideBarItem>
                        <AiOutlineHome size="20px" color = "#F8f8f8"/>
                        <Link to ="./">Home</Link>
                    </SideBarItem>
                    <SideBarItem>
                        <AiOutlineHome size="20px" color = "#F8f8f8"/>
                        <Link to ="./employee">Employee</Link>
                    </SideBarItem>
                    <SideBarItem>
                        <AiOutlineHome size="20px" color = "#F8f8f8"/>
                        <Link to ="./salary">Salary</Link>
                    </SideBarItem> */}
                </div>
                <div className="bot" style={{width : "90%"}}>
                    <SideBarItem>
                        <AiOutlineHome size="20px" color = "#F8f8f8"/>
                        <Link to ="">Log out</Link>
                    </SideBarItem>
                </div>
                
            </SideBarStyled>
        </div>
    )
}
