import styled from 'styled-components';

import {TextBlackHover} from '../text/index'

// const SideBarStyled = styled`
    
// `

const NavBarStyled = styled.nav`
    display : flex;
    align-items : center;
    width : 100%;
    padding : 20px;
    position : fixed ;
    top : 0;
    left : 0;
    background : #f8f8f8;
`
const NavBarItemsStyled = styled.a`
    display : inline-flex;
    text-decoration : none !important;
    margin : 0 20px;
    padding : 10px 20px;
    color : #242424;
    border-radius : 15px;
    cursor : pointer;
    &:hover{
        color : #242424;
        box-shadow: 0px 14px 28px rgba(0, 0, 0, 0.15);
    };

    &:focus {
      color: #242424;
    }
`

export const NavBar= ({items}) =>{
    return (
        <>
        <NavBarStyled>
            <TextBlackHover fontSize ="20px" fontWeight ="500">
                Coffee System
            </TextBlackHover>
            <NavBarItemsStyled>
                Employee
            </NavBarItemsStyled>
        </NavBarStyled>
        </>
    )
}


export const SideBar= () =>{
    return (
        <>
        <sideBarStyled>
            This is sidebar
        </sideBarStyled>
        </>
    )
}
