import {Row , Col} from 'reactstrap';
import styled from 'styled-components';
// import { useRecoilState, useRecoilValue} from 'recoil'
import {FiSearch} from 'react-icons/fi'
import {IoNotificationsOutline} from 'react-icons/io5'
import Avatar from 'react-avatar';

import {TextBlack} from '../text/index'
import {CustomInput} from '../login/index'


const NavBarStyled = styled.nav`
    display : flex;
    align-items : center;
    width : 100%;
    padding : 20px;
    position : fixed ;
    top : 0;
    left : 0;
    background : #ffff;
    z-index : 3;
`
const NavBarMidItems = styled.div`
    display : flex;
    padding : 0px;
    justify-content : space-between;
`
const NavBarEndItems = styled(NavBarMidItems)`
    justify-content : flex-end;
`

export const NavBar= () =>{
    
    return (
    <Row>
    <NavBarStyled>
        <Col lg="2">
            <TextBlack fontSize ="20px" fontWeight ="500">
                Coffee System
            </TextBlack>
        </Col>
        <Col lg="7">
            <TextBlack fontSize="24px" fontWeight="500">Welcome ! user</TextBlack>
            <CustomInput
                style = {{marginLeft : "30px"}}
                name = "Search"
                width = "300px"
                height = "35px"
            />
        </Col>
        <Col lg="3">
            <IoNotificationsOutline width="30px" height="30px"/>
            <Avatar  size="50" round={true}/>
        </Col>
    </NavBarStyled>
    </Row>
    )
}
