import {Row , Col} from 'reactstrap';
import styled from 'styled-components';
// import { useRecoilState, useRecoilValue} from 'recoil'
import {FiSearch} from 'react-icons/fi'
import {IoNotificationsOutline} from 'react-icons/io'
import Avatar from 'react-avatar';

import {TextBlack} from '../text/index'
import {CustomInput} from '../login/index'


const NavBarStyled = styled.nav`
    display : flex;
    align-items : center;
    width : 100%;
    padding : 20px;
    position : fixed ;
    top : 0;
    left : 0;
    background : #ffff;
    z-index : 3;
`
const NavBarItemsStyled = styled.a`
    display : inline-flex;
    text-decoration : none !important;
    margin : 0 20px;
    padding : 10px 20px;
    color : #242424;
    border-radius : 15px;
    cursor : pointer;
    background-color : ${props => props.active ? "#fcfcfc" : "#fffff"};
    transition-duration : 0.3s;
    &:hover{
        color : #242424;
        background-color : #fcfcfc;
        box-shadow: 0px 14px 28px rgba(0, 0, 0, 0.15);
    };

    &:focus {
      color: #242424;
    }
`

export const NavBar= () =>{
    
    return (
        <>
        
        <NavBarStyled>
            <Row>
            <Col lg="2">
                <TextBlack fontSize ="20px" fontWeight ="500">
                    Coffee System
                </TextBlack>
            </Col>
            <Col lg="7">
                <CustomInput
                    style = {{marginLeft : "30px"}}
                    name = "Search"
                    width = "300px"
                    height = "35px"
                />
            </Col>
            <Col lg="3">
                <IoNotificationsOutline/>
                <Avatar  size="30" round={true}/>
            </Col>
            </Row>
        </NavBarStyled>
        </>
    )
}
