import React from 'react'
import styled from 'styled-components'
import Avatar from 'react-avatar';
import {TextBlack} from '../text/index'

const Card = styled.div`
    width : 100%;
    height : 200px;
    border-radius : 10px;
    background-color : #ffff;
    padding : 20px 30px ;
    display : flex ;
    flex-direction : column;
    align-items : center;
    animation-duration : 0.2s;
    text-align : center;
    &:hover{
        box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.15);   
    }
`

export default function AvatarSquareCard({avatar , name , position, email}) {
    return (
        <div>
            <Card>
            <Avatar facebookId="Ken.deep.try.20" size="50" round={true}/>
                <TextBlack 
                    fontSize="16px" 
                    fontWeight ="500"
                >Vo Phu Hoang Nhat</TextBlack>
                <TextBlack fontSize="14px" fontWeight ="300">Position</TextBlack>
                <TextBlack fontSize="14px" fontWeight ="light">Lorem ipsum dolor sit amet consectetur </TextBlack>
            </Card>
        </div>
    )
}
