import styled from 'styled-components'
import { CloseBlackButton } from './buttons/index'

const Tab = styled.div`
    background: rgba(4, 4, 4, 0.7);
    backdrop-filter: blur(25px);
    width: 300px;
    height: 100px;
    border-radius: 20px;
    position: fixed;
    top: 10px;
    right: 10px;
    z-index: 2;

    &:hover{
        margin-top : 10px;
    }

`
const Toast = ({child}) => {
    return(
        <Tab>
            This is a notificate
            <CloseBlackButton>
            {child}
            </CloseBlackButton>
        </Tab>
    )
}

export default Toast;
