import React from 'react'
import styled from 'styled-components'



export const CommonCard = styled.div`
    width : 90%;
    height : 350px;
    padding : 30px 40px;
    background : #ffff;
    border-radius : 20px;
    &:hover{
        box-shadow: 0px 10px 25px rgba(0, 0, 0, 0.1);
    }
`
