import styled from 'styled-components';
import { AiOutlineHome } from "react-icons/ai";
import { useRecoilState } from 'recoil'

import {
    Link
} from 'react-router-dom'

import {activeItem} from '../../states/navbarState'

const SideBarStyled = styled.div`
    display : flex;
    flex-direction : column;
    align-items : center;
    height : 550px;
    justify-content : space-between;
    background-color : #ffff;
    height : 100%;
`
const SideBarItem = styled.div`
    display : flex;    
    border-radius: 15px;
    justify-content: space-around;
    padding : 15px 30px;
    margin-top : 30px;
    align-items : center;
    cursor : pointer;
    
    background-color : ${props => props.active ? "#242424" : "#ffff"};
    transition-duration : 0.2s;
    &>a,svg{
        font-weight : 500;
        color : ${props => props.active ? "#f8f8f8 !important" : "#242424 !important"};
        text-decoration : none;
    }
    &:hover{
        &>a,svg{   
            color : #f8f8f8 !important;
        }
        background-color : #242424;
        text-decoration : none;
    }

`



export default function SideBar({items}) {
    //func
    const [itemState , setItemState] = useRecoilState(activeItem)
    const handleClick = (id) => {
        const newList = itemState.map((item) => {
            item.active = false
            if (item.id === id) {
            const updatedItem = {
                ...item,
                active: !item.active,
            };
    
            return updatedItem;
            }
    
            return item;
        });
    
        setItemState(newList);
    }
    return (
        <div className="">
            <SideBarStyled>
                <div className="top" style={{width : "90%"}}>
                    {
                        items.map((item)  =>
                        <Link to ={item.path}>
                        <SideBarItem onClick={() => handleClick(item.id)}>
                            <AiOutlineHome active={item.active ? true : false} size="20px" color = "#F8f8f8"/>
                            {item.name}
                        </SideBarItem>
                        </Link>)
                        
                    }
                    {/* <SideBarItem>
                        <AiOutlineHome size="20px" color = "#F8f8f8"/>
                        <Link to ="./">Home</Link>
                    </SideBarItem>
                    <SideBarItem>
                        <AiOutlineHome size="20px" color = "#F8f8f8"/>
                        <Link to ="./employee">Employee</Link>
                    </SideBarItem>
                    <SideBarItem>
                        <AiOutlineHome size="20px" color = "#F8f8f8"/>
                        <Link to ="./salary">Salary</Link>
                    </SideBarItem> */}
                </div>
                <div className="bot" style={{width : "90%"}}>
                    <SideBarItem>
                        <AiOutlineHome size="20px" color = "#F8f8f8"/>
                        <Link to ="">Log out</Link>
                    </SideBarItem>
                </div>
                
            </SideBarStyled>
        </div>
    )
}
