import {atom} from 'recoil'

export const activeItem = atom({
    key: 'activeItem', // unique ID (with respect to other atoms/selectors)
    default: [
            {   
                id : "1",
                href : "./employee",
                name : "Employee",
                active : false,
                child : [
                    {
                        id : "1",
                        href : "./employee/list",
                        name : "List",
                        active : false
                    }
                ]
            },
            {
                id : "2",
                href : "./request",
                name : "Request",
                active : false,
                child : [
                    {
                        id : "1",
                        href : "./request/list",
                        name : "List Request",
                        active : false
                    }
                ]
            },
        ], // default value (aka initial value)
        
    });
