// import React, {useState} from 'react';
import { Row, Col } from 'reactstrap';
import { gql, useMutation } from '@apollo/client';
import { useForm } from "react-hook-form";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

//import components
import Image from '../public/img.jpg'
import {
    FormLogin,
    ContainerMiddle,
    TitleH1,
    BigImage,
    InputItems,
    CustomInput
} from '../components/login'
import { 
    PrimaryButton,
    SecondaryButton,
 } from "../components/buttons";
import { 
    TextBlack,
    TextLight,
    TextBlackHover,
    TextError
 } from "../components/text";

export default function Login() {
    const { register, handleSubmit, errors } = useForm();
    const LOGIN_ADMIN = gql`
        mutation loginAdmin($username: String! , $password : String!) {
            loginAdmin(username: $username , password : $password) {
                id
                username
                token
            }
        }
    `
    const [loginAdmin , {data}] = useMutation(LOGIN_ADMIN);
    const onSubmit = async dataSubmit => {
        try{
            //submit data
            loginAdmin({
                variables : 
                {
                    username : dataSubmit.username,
                    password : dataSubmit.password
                }
            })
            //data ? success
            if(data) 
            toast('ðŸ¦„ Wow so easy!', {
                position: "top-right",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }catch(err){
            
        }
        
        console.log(data);
        console.log(dataSubmit);
    };

    return (    
        <ToastContainer>
        <div>
            <FormLogin onSubmit={handleSubmit(onSubmit)} >
            <Row lg="2">
                <Col>
                <ContainerMiddle>
                    <TitleH1 >Login</TitleH1>
                    <TextBlack fontWeight ="300" fontSize = "14px">Starting manage your team</TextBlack>
                    <InputItems>
                        <TextBlack fontWeight ="500" fontSize = "16px">Username</TextBlack>
                        <CustomInput  name="username" height = "40px" placeholder="Username ..." ref={register({ required: true })} />
                        {errors.username && <TextError>This field is required</TextError>}
                    </InputItems>
                    <InputItems>
                        <TextBlack fontWeight ="500" fontSize = "16px">Password</TextBlack>
                        <CustomInput  name="password" type="password"  height = "40px" placeholder="Password ..." ref={register({ required: true })} />
                        {errors.password && <TextError>This field is required</TextError>}
                    </InputItems>
                    <InputItems>
                        <TextBlackHover fontWeight ="500" fontSize = "14px">Fogot Password ?</TextBlackHover>
                    </InputItems>
                    <InputItems>
                    <Row>
                        <Col lg="8">
                            <PrimaryButton type="submit"> 
                                <TextLight fontWeight = "500" fontSize="14px">Sign In</TextLight> 
                            </PrimaryButton>
                        </Col>
                        
                        <Col lg="4">
                            <Link to='/register'>
                                <SecondaryButton>
                                    <TextBlack fontWeight = "300" fontSize="14px">
                                        Sign Up
                                    </TextBlack>
                                </SecondaryButton>
                            </Link>
                        </Col>
                    </Row>
                    </InputItems>
                </ContainerMiddle>
                </Col>
                <Col>
                    <BigImage url={Image} ></BigImage>
                </Col>
            </Row>
            </FormLogin>
        </div>
        </ToastContainer>
    )
}
