import styled from 'styled-components';
import { AiOutlineHome } from "react-icons/ai";

import {
    Link
} from 'react-router-dom'
// import { useRecoilState } from 'recoil'

// import {activeItem} from '../../states/navbarState'

import {TextLight} from '../text/index'
// import {CustomInput} from '../login/index'
const SideBarStyled = styled.div`
    display : flex;
    flex-direction : column;
    align-items : center;
    background-color : #ffff;
    height : 100%;
`
const SideBarItem = styled.div`
    display : flex;    
    border-radius: 15px;
    width: 90%;
    justify-content: space-between;
    padding : 15px 30px;
    margin-top : 30px;
    align-items : center;
    color : #242424;
    /* background-color : #242424; */
    transition-duration : 0.2s;

    &:hover{
        color : #f8f8f8;
        background-color : #242424;
    }

`

export default function SideBar() {
    return (
        <div className="">
            <SideBarStyled>
                <SideBarItem>
                    <AiOutlineHome size="20px" color = "#F8f8f8"/>
                    <Link to ="./">Home</Link>
                </SideBarItem>
                <SideBarItem>
                    <AiOutlineHome size="20px" color = "#F8f8f8"/>
                    <TextLight>
                        List Employee
                    </TextLight>
                </SideBarItem>
                <SideBarItem>
                    <AiOutlineHome size="20px" color = "#F8f8f8"/>
                    <TextLight>
                        List Employee
                    </TextLight>
                </SideBarItem>
            </SideBarStyled>
        </div>
    )
}
