import { gql } from "@apollo/client";
export const LOGIN = gql`
  mutation loginUser($username: String!, $password: String!) {
    loginUser(username: $username, password: $password) {
      id
      username
      position
      token
    }
  }
`;

export const CREATE_NEW_EMPLOYEE = gql`
  mutation createNewEmployee(
    $username: String!
    $password: String!
    $name: String!
    $phone: String
    $email: String!
    $position: String!
    $address: String
  ) {
    createNewEmployee(
      username: $username
      password: $password
      name: $name
      phone: $phone
      email: $email
      position: $position
      address: $address
    ) {
      id
      username
      position
    }
  }
`;
