import React, {useEffect} from 'react'
import styled from 'styled-components'
import {CommonCard} from '../../components/cards/CommonCard'
import { Table } from 'reactstrap';
import ReactLoading from 'react-loading';
import { toast } from 'react-toastify';

import { gql, useQuery  } from '@apollo/client';

export default function Employee() {

    //fetch data
    const GET_ALL_EMPLOYEES = gql`
        query getAllEmployees{
                id
                name
                username
                email
                phone
                position
                createdAt
                updateAt
            }
    `

    const { loading, error, data } = useQuery(GET_ALL_EMPLOYEES);

    if (error){
        toast(`⛔ ${error}`);
    }

    return (
        <div style={{overflowX : "scroll" , padding : "10px"}}>
            <CommonCard width={"100%"}>
            {loading ? <ReactLoading type={"bars"} color={"#242424"} height={50} width={50} /> : 
                <Table hover responsive>
                    <thead>
                        <tr>
                        <th>#</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Position</th>
                        </tr>
                    </thead>
                    {
                        data.getAllEmployees.map(employee => 
                        <tbody>
                            <tr>
                            <th scope="row">1</th>
                            <td>Mark</td>
                            <td>Otto</td>
                            <td>@mdo</td>
                            </tr>
                        </tbody>
                        )
                    }
                </Table>
            }
            
            </CommonCard>
        </div>
    )
}
